const searchIndex = new FlexSearch.Document({ charset: "latin", tokenize: "forward", language: "de", minlength: 3, document: { id: "id", index: ["heading", "content"] } });
quowadisArticles.forEach((article, i) => searchIndex.add(i, article));

let depth = (function () {
  let count = 0;
  let url = window.location.href;
  while ((match = url.match(/\/([^\/]*)$/)) != null) {
      url = url.substring(match.index, -1);
      if (["assets", "files", "user"].includes(match[1]))
        return count;
      count++;
  }
  return 0;
})();

let toLocalUrl = (url) => "." + "/..".repeat(depth) + url + ".html";

function searchArticles(query) {
    let searchResult = searchIndex.search(query, [{ field: "heading", limit: 10 }, { field: "content", limit: 15 }]);
    let headingIDs = searchResult.find(a => a.field == "heading")?.result;
    let contentIDs = searchResult.find(a => a.field == "content")?.result;
    
    let ul = document.createElement("ul");
    ul.classList.add("search-result-list");
    ul.setAttribute("data-search-found-amount", (headingIDs?.length ?? 0) + (contentIDs?.length ?? 0));

    if (headingIDs) {
        let h3 = document.createElement("h3");
        h3.textContent = "In Titel";
        ul.append(h3);
        for (let id of headingIDs) {
            let articleElem = createArticle(id, query);
            ul.appendChild(articleElem);
        }
    }

    if (contentIDs) {
        let h3 = document.createElement("h3");
        h3.textContent = "Auf Seite";
        ul.append(h3);
        for (let id of contentIDs) {
            let articleElem = createArticle(id, query);
            ul.appendChild(articleElem);
        }
    }

    return ul.outerHTML;
}

function createArticle(articleID, query) {
    let article = quowadisArticles[articleID];
    article.content = showMatches(article.content, query);

    let li = document.createElement("li");
    li.classList.add("search-entry");

    let nav = document.createElement("div");
    for (let section of article.sections) {
        let a = document.createElement("a");
        a.href = toLocalUrl(section[0]);
        a.textContent = section[1];
        nav.append(a, " > ");
    }
    nav.removeChild(nav.lastChild);

    let h2 = document.createElement("h2");
    h2.classList.add("search-entry-title");

    {
        let a = document.createElement("a");
        a.href = toLocalUrl(article.href);

        let span1 = document.createElement("span");
        span1.classList.add("text-primary");
        span1.textContent = article.heading[0];

        a.appendChild(span1);

        if (article.heading[1]) {
            let span2 = document.createElement("span");
            span2.classList.add("text-body");
            span2.textContent = article.heading[1];
            a.append(" ", span2);
        }

        h2.appendChild(a);
    }

    let p = document.createElement("p");
    p.innerHTML = article.content;
    li.appendChild(p);

    li.append(nav, h2, p);

    return li;
}

function showMatches(content, query, length = 150) {  
  let intervals = query.split(/\s/).flatMap(searchTerm => Array.from(content.matchAll(searchTerm)).map(match => { return { index: match.index, length: match[0].length } })).sort((a, b) => a.index - b.index);
  let chars = intervals.flatMap(elem => Array.from({ length: elem.length }).map((_, i) => elem.index + i));

  let start = Array.from({ length: content.length - length })
    .map((_, i) => chars.filter(char => char >= i && char < i + length)
    .reduce((p, c) => p + Math.abs(c - (i + length / 2)), 0)).reduce((prev, val, i) => {
      if (prev.val < val) return { i, val };
      else return prev
    }, { i: -1, val: -1 }).i;
  
  return content.substring(start, start + length);
}

